/* eslint-disable jsx-a11y/alt-text */
/* eslint-disable jsx-a11y/anchor-is-valid */
import React, {useState} from 'react';
import {CategoryList} from './category.js';
import { FaArrowAltCircleLeft, FaArrowAltCircleRight } from 'react-icons/fa';
import './style.css';

export default function Category({category}) {
  

  if(!Array.isArray(category) || category.length <=0){
    return null;
  }

  return (
      <section className='categories'>
        {CategoryList.map((categories, index)=> {
          return(
                <div className='categories-inter'>
                  <a href={categories.url} className='categories-a'>
                    <img src={categories.image} className='categories-img'/>
                    <h2 className='slider-h2'>{categories.title}</h2>
                  </a>
                </div>
          )
        })}
      </section>
  );
}
